import React, {useState} from 'react'
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";

function PaymentDataForm() {
const [number, setNumber] = useState("");
const [expiry, setExpiry] = useState("");
const [cvc, setCvc] = useState("");
const [focus, setFocuse] = useState("");

return (
  <div className="App">
    <Cards number={number} expiry={expiry} cvc={cvc} focused={focus} />
    <form>
      <label htmlFor="duration">Duration</label>
      <select id="duration">
        <option>3</option>
        <option>6</option>
        <option selected>12</option>
      </select>
      <label>Amount</label>
      <select>
        <option value="5" selected>
          5
        </option>
        <option value="10">10</option>
        <option value="50">50</option>
      </select>
      <label>upform payment</label>
      <input type="radio" name="payment" value="yes" />
      Yes
      <input type="radio" name="payment" value="no" />
      No
      <input
        type="tel"
        name="number"
        placeholder="Card Number"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
        onFocus={(e) => setFocuse(e.target.name)}
      />
      <input
        type="text"
        name="expiry"
        placeholder="MM/YY Expiry"
        value={expiry}
        onChange={(e) => setExpiry(e.target.value)}
        onFocus={(e) => setFocuse(e.target.name)}
      />
      <input
        type="tel"
        name="cvc"
        placeholder="CVC"
        value={cvc}
        onChange={(e) => setCvc(e.target.value)}
        onFocus={(e) => setFocuse(e.target.name)}
      />
      <button type="submit"> Comfirm</button>
    </form>
  </div>
);
}

export default PaymentDataForm
